export const patientState = { patients: [] }

export const patientReducer = (state, action) => {
    switch (action.type) {
        case 'ADD_PATIENT':
            const newPatient = {
                id: action.id,
                name: action.name,

            }
            const allPatients = [...state.patients]
            return [allPatients, newPatient];
        case 'REMOVE_PATIENT':
            return state;
        default:
            return state;

    }
}
